import React, {
    Component
} from 'react';

import { Table, Spin, message, Popover, Tabs, Button } from 'antd';
import { AppAPI } from '../../../api/appApi';

const { TabPane } = Tabs;
const columns = [
    {
        title: 'Mã nhân sự',
        width: 120,
        dataIndex: 'hrcode',
