import React, {
    Component
} from 'react';
import Breadcrumb from '../../master/main/breadcrumb';
import { Button, Tag, Popover, Select, Row, Col, Input, DatePicker } from 'antd';
import { AiOutlinePlus } from "react-icons/ai";
import { BsTrash } from "react-icons/bs";
import { FaFilter } from "react-icons/fa";

const { Option } = Select;

class Filter extends Component {
    state = {
        filterBy: 'product',
        filterIs: 'is',
        textFilterBy: [
            {text: 'Mua theo sản phẩm', key: 'product'},
            {text: 'Số lần mua hàng', key: 'count_by'},
            {text: 'Tỉnh thành', key: 'provin'},
            {text: 'Quận huyện phường', key: 'district'},
            {text: 'Công ty chi nhánh', key: 'branch'},
            {text: 'Thời gian', key: 'date'},
        ],
        filterValue: "",
        visibleText: true,
        visibleDate: false,
    }

    updateFilterBy = e => {
        this.setState({filterBy: e});
    }

    updateFilterIs = e => {
        e == 'date' ? this.setState({visibleText: false, visibleDate: true}) : this.setState({visibleText: true, visibleDate: false});
        this.setState({filterIs: e});
    }

    updateValue = e => {
        typeof e.target.value == "undefined" ? this.setState({filterValue: e}) : this.setState({filterValue: e.target.value});
    }

    render() {
        return (
            <div style={{ width: '550px' }}>
                <div className="filter_by_wrapper">
                    <div className="title mb-10">Tìm kiếm theo</div>
                    <Row gutter={16}>
                        <Col className="gutter-row" span={8}>
                            <Select onChange={(e) => { this.updateFilterBy(e) }}  defaultValue={this.state.filterBy} className="w-100">
                                <Option value="product">Mua theo sản phẩm</Option>
                                <Option value="count_buy">Số lần mua hàng</Option>
                                <Option value="provin">Tỉnh thành</Option>
                                <Option value="district">Quận huyện phường</Option>
                                <Option value="brach">Công ty chi nhánh</Option>
                                <Option value="date">Thời gian</Option>
                            </Select>
                        </Col>
                        <Col className="gutter-row" span={6}>
                            <Select onChange={(e) => { this.updateFilterIs(e) }} defaultValue={this.state.filterIs} className="w-100">
                                <Option value="is">Là</Option>
                                <Option value=">">Lớn hơn</Option>
                                <Option value="<">Bé hơn</Option>
                            </Select>
                        </Col>
                        <Col className="gutter-row" span={8}>
                            <Input onChange={e => this.updateValue(e)} value={this.state.filterValue} className={this.state.visibleText ? 'd-block w-100' : 'd-none w-100'} />
                            <DatePicker onChange={e => this.updateValue(e)} value={this.state.filterValue} className={this.state.visibleDate ? 'd-block w-100' : 'd-none w-100'}  />
                        </Col>
                        <Col className="gutter-row d-flex align-items-center justify-content-center fs-16 " span={2}>
                            <BsTrash />
                        </Col>
                    </Row>
                </div>
                <div className="sort_by_wrapper mt-10">
                    <Button type="primary" onClick={e => this.addFilter(e)} icon={<FaFilter />}>Tìm kiếm</Button>
                </div>
            </div>
        )
    }

    addFilter = function () {
        let filterIs = this.state.filterIs.replace("is", 'là');

        this.state.textFilterBy.map((item, index) => {
            if(item.key == this.state.filterBy) {
                this.props.updateFilter(item.text, filterIs, this.state.filterValue);
            }
        });
    }
}

class Header extends Component {
    state = {
        filter: [
            { text: "Khách hàng mua sản phẩm Dakami" },
            { text: "Khách hàng mua từ tháng 55/2021" }
        ],
        url: "",
        visible: false
    }

    updateFilter = (filterBy, filterIs, filterValue) => {
        let obj = {text: filterBy + " " + filterIs + " " + filterValue};

        this.setState(prevState => ({
            filter: [...prevState.filter, obj]
        }));
    }

    render() {
        return (
            <section className="buttonaction buttonaction--layout">
                <Breadcrumb>Phân tích và tìm kiếm tệp khách hàng</Breadcrumb>
                <div className="keyword_suggest_wrapper">
                    <div className="keyword__container d-flex align-items-center">
                        <div className="keyword__label">Tiêu chí lọc: </div>
                        <div className="keyword__items d-flex flex-1 mx-20">
                            {this.state.filter.map(item => (
                                <Tag color="purple" closable>{item.text}</Tag>
                            ))}
                            <Popover
                                content={<Filter updateFilter={this.updateFilter} filter={this.state.filter} />}
                                title="Thêm điều kiện lọc"
                                trigger="click"
                                placement="bottom"
                            >
                                <Button type="primary" className="d-flex align-items-center" size="small" icon={<AiOutlinePlus />}>Thêm điều kiện lọc</Button>
                            </Popover>

                        </div>
                    </div>
                </div>
            </section>
        );
    }
}


export default Header;
