import React, {
    Component
} from 'react';
import Header from './header';
import Main from './main';
import './style.sass';
import { message, Button } from 'antd';
import { AppAPI } from '../../../api/appApi';

class Analytics extends Component {
    state = {
        columnState: [{
            title: '#',
            width: 90,
            dataIndex: 'id',
            key: 'id',
            fixed: 'left',
        }],
        dataSource: [],
        loading: true,
        params: []
    }

    componentDidMount() {
        this.fetchListDataFromAPI();
        this.fetchColumn();
    }

    render() {
        let { loading, dataSource, columnState } = this.state;
        return (
            <div className="d-flex flex-column h-100">
                <Header updateDataTable={this.updateDataTable} />
                <Main loading={loading} dataSource={dataSource} columnState={columnState} />
            </div>
        );
    }

    updateDataTable = (arrayFilter) => {
        let params = arrayFilter;

        this.setState(prevState => ({
            params: [...prevState.params, params]
        }), function() { console.log("setState completed", this.state.params) });

        setTimeout(() => {
            console.log(this.state.params);
        }, 500);

        // this.fetchListDataFromAPI(params);
    }

    fetchListDataFromAPI = (params = '') => {
        this.setState({ loading: true });
        AppAPI.getAllData(params).then(res => {
            const { data } = res;
            var stt = 0;
            if (data.success) {
                data.data.map(res => {
                    stt += 1;
                    let obj = res;
                    this.setState(prevState => ({
                        dataSource: [...prevState.dataSource, obj]
                    }));
                });
            }
        }).finally(res => {
            this.setState({ loading: false });
        })
    }

    fetchColumn = () => {
        AppAPI.getAllColumn().then(res => {
            const { data } = res;
            var stt = 0;
            if (data.success) {
                data.data.map(res => {
                    stt += 1;

                    const obj = {
                        title: res.title,
                        dataIndex: res.name,
                        key: res.name,
                        width: res.width ?? 250,
                        fixed: res.fixed ?? 'none'
                    };

                    if (res.show || typeof res.show == "undefined") {
                        this.setState(prevState => ({
                            columnState: [...prevState.columnState, obj]
                        }));
                    }

                });

                this.setState({
                    columnState: [...this.state.columnState, {
                        title: 'Chi tiết',
                        width: 90,
                        dataIndex: 'detail',
                        key: 'detail',
                        fixed: 'right',
                        render: () => <Button type="primary">Xem</Button>,
                    }]
                });
            } else {
                message.error(data.msg);
            }
            return true;
        });
    }
}

export default Analytics;
