import React, {
    useState, useEffect
} from 'react';
import { AppAPI } from '../../../api/appApi';
import { Drawer, Skeleton, Button } from 'antd';


function Detail(props) {
    const [loading, setLoading] = useState(true);
    const getDetail = (id) => {
        AppAPI.getDetailData(id).then(res => {
            setLoading(false);
        })
    }

    useEffect(() => {
        if (!!props.id) {
            getDetail(props.id);
        }
    }, [props.id]);

    return (
        <Drawer
            title="Thông tin khách hàng"
            placement={'right'}
            closable={false}
            onClose={props.hideDetail}
            visible={props.visibleDetail}
            key={'right'}
            width="600px"
            footer={
                <div
                    style={{
                        textAlign: 'right',
                    }}
                >
                    <Button type="primary" onClick={() => {
                        form
                            .validateFields()
                            .then(values => {
                                form.resetFields();
                                let value = values;
                                if (!isCreate && detailtCustomerState.length != 0) {
                                    value = Object.assign(values, { _id: detailtCustomerState._id });
                                }
                                onCreate(value);
                            });
                    }} htmlType="submit"> {isCreate ? "Thêm mới" : "Cập nhật"} </Button>

                    <Button
                        style={{ margin: '0 8px' }}
                        onClick={() => {
                            form.resetFields();
                        }}
                    >
                        Xoá
                    </Button>
                </div>
            }>
            <Skeleton loading={loading} active />
            <Skeleton loading={loading} active />
            <Skeleton loading={loading} active />
        </Drawer>
    );
}



export default Detail;
