import React, {
    useState, useEffect
} from 'react';
import { AppAPI } from '../../../api/appApi';
import { Drawer, Skeleton, Button, Form, Row, Input, Select, DatePicker, Divider} from 'antd';

const { Option } = Select;
const { Col } = Row;

const content = (
    <Form layout="vertical">
        <Row>
            <Col span={24}>
                <Form.Item
                    name="name"
                    label="Tên nhân viên"
                    rules={[{ required: true, message: 'Vui lòng nhập tên nhân viên' }]}
                >
                    <Input placeholder="Tên nhân viên" />
                </Form.Item>
            </Col>
        </Row>
        <Row>
            <Col span={24}>
                <Form.Item
                    name="description"
                    label="Mô tả nhân viên"
                >
                    <Input.TextArea placeholder="Team nhân viên, mô tả khi trùng tên nhân sự" rows={2} />
                </Form.Item>
            </Col>
        </Row>
        <Row>
            <Col span={12}>
                <Form.Item
                    name="phone"
                    label="Số điện thoại"
                >
                    <Input placeholder="Số điện thoại" />
                </Form.Item>
            </Col>
            <Col span={12}>
                <Form.Item
                    name="email"
                    label="Địa chỉ email"
                    style={{ width: 'calc(100% - 8px)', margin: '0 8px' }}
                >
                    <Input placeholder="Địa chỉ email" />
                </Form.Item>
            </Col>
        </Row>
        <Row>
            <Col span={12}>
                <Form.Item
                    name="gender"
                    label="Giới tính"
                    rules={[{ required: true, message: 'Vui lòng nhập giới tính' }]}
                >
                    <Select>
                        <Option value="1">Nam</Option>
                        <Option value="0">Nữ</Option>
                    </Select>
                </Form.Item>
            </Col>
            <Col span={12}>
                <Form.Item
                    name="dateofbirth"
                    label="Ngày sinh"
                    style={{ width: 'calc(100% - 8px)', margin: '0 8px' }}
                >
                    <DatePicker className="w-100" />
                </Form.Item>
            </Col>
        </Row>

        <Divider orientation="left">Thông tin công việc</Divider>

        <Row>
            <Col span={12}>
                <Form.Item
                    name="department"
                    label="Phòng ban"
                    rules={[{ required: true, message: 'Vui lòng nhập phòng ban' }]}
                >
                    <Select>

                    </Select>
                </Form.Item>
            </Col>
            <Col span={12}>
                <Form.Item
                    name="level"
                    label="Chức vụ làm việc"
                    style={{ width: 'calc(100% - 8px)', margin: '0 8px' }}
                >
                    <Input placeholder="Chức vụ làm việc" />
                </Form.Item>
            </Col>
        </Row>
        <Row>
            <Col span={24}>
                <Form.Item
                    name="date_start_working"
                    label="Ngày bắt đầu làm việc"
                    rules={[{ required: true, message: 'Vui lòng nhập ngày bắt đầu làm việc' }]}
                >
                    <DatePicker className="w-100" />
                </Form.Item>
            </Col>
        </Row>
    </Form>
);

function Detail(props) {
    const [loading, setLoading] = useState(true);
    const getDetail = (id) => {
        AppAPI.getDetailData(id).then(res => {
            setLoading(false);
        })
    }

    useEffect(() => {
        if (!!props.id) {
            getDetail(props.id);
        }
    }, [props.id]);

    const closeForm = () => {

    }

    const closeFormChild = () => {

    }

    const onCreate = values => {
        AppAPI.createCustomer(values).then(res => {

        });
    }

    return (
        <Drawer
            title="Thông tin khách hàng"
            placement={'right'}
            closable={false}
            onClose={props.hideDetail}
            visible={props.visibleDetail}
            key={'right'}
            width="600px"
        >
            <Skeleton loading={loading} active />
            <Skeleton loading={loading} active />
            <Skeleton loading={loading} active />
            {!loading && content}
        </Drawer>
    );
}



export default Detail;
