import React, {
    Component
} from 'react';

import { Table, Spin, message, Popover, Tabs, Button } from 'antd';
import { AppAPI } from '../../../api/appApi';


class Main extends Component {
    state = {
        loading: true,
        fingerLog: "",
        dataSource: [],
        prefixName: "",
        columnState: [
            {
                title: '#',
                width: 90,
                dataIndex: 'id',
                key: 'id',
                fixed: 'left',
            }]
    }
    componentDidMount() {
        this.fetchListDataFromAPI();
        this.fetchColumn();
    }

    render() {
        const data = [];
        const { loading, dataSource, columnState, prefixName } = this.state;

        return (
            <section className="main-dashboard main-dashboard--layout overflow-auto flex-1">
                <Spin spinning={loading} tip="Đang tải dữ liệu">
                    <Table columns={columnState} dataSource={dataSource} pagination={false} size="middle" scroll={{ x: 'calc(700px + 50%)', y: 'calc(100vh - 330px)' }} />
                    <article className="main-dashboard__bottom p-5 d-flex justify-content-center">
                        <Button className="mr-10" type="primary" danger>Xuất ra file Excel</Button>
                        <Button className="mr-10" type="primary" danger>Xuất ra API Json</Button>
                        <Button className="mr-10" type="primary">Lưu tệp khách hàng</Button>
                    </article>
                    <article className="main-dashboard__bottom">
                        <div className="keyword_suggest_wrapper">
                            <div className="keyword__container d-flex align-items-center">
                                <div className="keyword__label">Tệp khách hàng gợi ý: </div>
                                <div className="keyword__items d-flex flex-1 mx-10">
                                    <div className="keyword__item">Khách hàng có địa chỉ tại hà nội</div>
                                    <div className="keyword__item">Khách hàng đã từng mua 2 lần trở lên</div>  
                                    <div className="keyword__item">Khách hàng từng mua sản phẩm Dakami</div>
                                </div>
                            </div>
                        </div>
                    </article>
                </Spin>
            </section>
        );
    }

    fetchListDataFromAPI = () => {
        AppAPI.getAllData().then(res => {
            const { data } = res;
            var stt = 0;
            if (data.success) {
                data.data.map(res => {
                    stt += 1;
                    let obj = res;
                    this.setState(prevState => ({
                        dataSource: [...prevState.dataSource, obj]
                    }));
                });
            }
        }).finally(res => {
            this.setState({ loading: false });
        })
    }

    fetchColumn = () => {
        AppAPI.getAllColumn().then(res => {
            const { data } = res;
            var stt = 0;
            if (data.success) {
                data.data.map(res => {
                    stt += 1;

                    const obj = {
                        title: res.title,
                        dataIndex: res.name,
                        key: res.name,
                        width: res.width ?? 250,
                        fixed: res.fixed ?? 'none'
                    };

                    if (res.show || typeof res.show == "undefined") {
                        this.setState(prevState => ({
                            columnState: [...prevState.columnState, obj]
                        }));
                    }

                });

                this.setState({
                    columnState: [...this.state.columnState, {
                        title: 'Chi tiết',
                        width: 90,
                        dataIndex: 'detail',
                        key: 'detail',
                        fixed: 'right',
                        render: () => <Button type="primary">Xem</Button>,
                    }]
                });
            } else {
                message.error(data.msg);
            }
            return true;
        });
    }
}


export default Main;
