import React, {
    useState, useEffect
} from 'react';
import { AppAPI } from '../../../api/appApi';
import { Drawer, Skeleton, Button, Form, Row, Input, Select, DatePicker, Divider } from 'antd';

const { Option } = Select;
const { Col } = Row;

const FormCreate: React.FC<CollectionCreateFromProps> = ({
}) => {
    const [form] = Form.useForm();
    form.resetFields();

    return (
        <Row>
            <Col span={24}>
                <Form.Item
                    name="name"
                    label="Tên nhân viên"
                    rules={[{ required: true, message: 'Vui lòng nhập tên nhân viên' }]}
                >
                    <Input placeholder="Tên nhân viên" />
                </Form.Item>
            </Col>
        </Row>
    );
}

function Detail(props) {
    const [loading, setLoading] = useState(true);
    const getDetail = (id) => {
        AppAPI.getDetailData(id).then(res => {
            setLoading(false);
        })
    }

    useEffect(() => {
        if (!!props.id) {
            getDetail(props.id);
        }
    }, [props.id]);

    const closeForm = () => {

    }

    const closeFormChild = () => {

    }

    const onCreate = values => {
        AppAPI.createCustomer(values).then(res => {

        });
    }

    return (
        <Drawer
            title="Thông tin khách hàng"
            placement={'right'}
            closable={false}
            onClose={props.hideDetail}
            visible={props.visibleDetail}
            key={'right'}
            width="600px"
        >
            <Skeleton loading={loading} active />
            <Skeleton loading={loading} active />
            <Skeleton loading={loading} active />
            <FormCreate />
        </Drawer>
    );
}



export default Detail;
