import React, {
    useState, useEffect
} from 'react';
import { AppAPI } from '../../../api/appApi';
import { Drawer, Skeleton, Button, Form, Row, Input, Select, Divider, Steps } from 'antd';
const { Step } = Steps;

const create = () => {
    alert();
}

const content = (
    <div>
        <Input.TextArea placeholder="Nội dung ghi chú" allowClear />
        <Button onClick={e => create(e)} className="mt-10" type="primary">Lưu</Button>
        <Divider orientation="left">Lịch sử ghi chú</Divider>
        <Steps direction="vertical" className="mt-20" current={1}>
            <Step title="Finished" description="This is a description." />
            <Step title="In Progress" description="This is a description." />
            <Step title="Waiting" description="This is a description." />
        </Steps>
    </div>

);

function Detail(props) {
    const [loading, setLoading] = useState(true);
    const getDetail = (id) => {
        AppAPI.getLogData(id).then(res => {
            setLoading(false);
        })
    }

    useEffect(() => {
        if (!!props.id) {
            getDetail(props.id);
        }
    }, [props.id]);

    const closeForm = () => {

    }

    const closeFormChild = () => {

    }

    const onCreate = values => {
        AppAPI.createCustomer(values).then(res => {

        });
    }

    return (
        <Drawer
            title="Thông tin khách hàng"
            placement={'right'}
            closable={false}
            onClose={props.hideDetail}
            visible={props.visibleDetail}
            key={'right'}
            width="600px"
        >
            <Skeleton loading={loading} active />
            <Skeleton loading={loading} active />
            <Skeleton loading={loading} active />
            {!loading && content}
        </Drawer>
    );
}



export default Detail;
